D = defaultdict(list)
for i, item in enumerate(wordInput):
  D[item].append(i)

D = {k:v for k,v in D.items() if len(v) > 1}

count = 0

# print(D)

for key, value in D.items():
  currentDIndex = 0

  while currentDIndex < len(value) - 1:
    
    i = 0
    while i < len (value) - 1:
      number =  value[i + 1] - value[currentDIndex] - 1
      if number >= 1:
        count += number
      i += 1
    
    currentDIndex += 1

print(int(count))
